import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useMutation } from '@tanstack/react-query';
import { FiTarget, FiInfo, FiZap, FiShield, FiClock } from 'react-icons/fi';
import { toast } from 'react-hot-toast';
import { apiService, isValidIP } from '../../utils/api';

const NewScan: React.FC = () => {
  const navigate = useNavigate();
  
  const [targetIP, setTargetIP] = useState('');
  const [scanName, setScanName] = useState('');
  const [autoGenerateName, setAutoGenerateName] = useState(true);
  const [isValid, setIsValid] = useState(true);

  const scanMutation = useMutation({
    mutationFn: ({ ip, name }: { ip: string; name?: string }) => 
      apiService.startScan(ip, name),
    onSuccess: (data) => {
      toast.success('Scan started successfully!');
      navigate(`/scan/${data.scan_id}/live`);
    },
    onError: (error: any) => {
      toast.error(error.response?.data?.detail || 'Failed to start scan');
    },
  });

  const handleIPChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const ip = e.target.value;
    setTargetIP(ip);
    
    if (ip.trim() === '') {
      setIsValid(true);
      return;
    }
    
    setIsValid(isValidIP(ip));
    
    if (autoGenerateName && isValidIP(ip)) {
      const date = new Date();
      const timestamp = date.toLocaleString('en-US', {
        month: 'short',
        day: 'numeric',
        hour: '2-digit',
        minute: '2-digit'
      });
      setScanName(`Scan ${ip} - ${timestamp}`);
    }
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    
    if (!isValid || !targetIP.trim()) {
      toast.error('Please enter a valid IP address');
      return;
    }
    
    scanMutation.mutate({
      ip: targetIP.trim(),
      name: scanName.trim() || undefined
    });
  };

  const scanFeatures = [
    {
      icon: <FiZap className="text-yellow-500" size={20} />,
      title: 'Lightning Fast',
      description: 'Scans top 50 ports in under 30 seconds'
    },
    {
      icon: <FiShield className="text-green-500" size={20} />,
      title: 'Security Focused',
      description: 'Prioritizes critical vulnerabilities'
    },
    {
      icon: <FiClock className="text-blue-500" size={20} />,
      title: 'Real-time Updates',
      description: 'Watch scans progress live'
    },
    {
      icon: <FiInfo className="text-purple-500" size={20} />,
      title: 'Detailed Reports',
      description: 'PDF exports with remediation steps'
    }
  ];

  return (
    <div className="max-w-6xl mx-auto">
      {/* Header */}
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900 dark:text-white">New Vulnerability Scan</h1>
        <p className="text-gray-600 dark:text-gray-400 mt-2">
          Quick and focused security assessment using Nmap
        </p>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Left Column - Scan Form */}
        <div className="lg:col-span-2">
          <div className="card">
            <div className="flex items-center mb-6">
              <div className="p-2 bg-blue-100 dark:bg-blue-900/30 rounded-lg mr-3">
                <FiTarget className="text-blue-600 dark:text-blue-400" size={24} />
              </div>
              <div>
                <h2 className="text-xl font-semibold text-gray-900 dark:text-white">
                  Scan Configuration
                </h2>
                <p className="text-gray-600 dark:text-gray-400">
                  Enter target details and start scanning
                </p>
              </div>
            </div>

            <form onSubmit={handleSubmit}>
              {/* Target IP */}
              <div className="mb-6">
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                  Target IP Address *
                </label>
                <input
                  type="text"
                  value={targetIP}
                  onChange={handleIPChange}
                  placeholder="e.g., 192.168.1.1"
                  className={`input-field ${!isValid && targetIP ? 'border-red-500 focus:ring-red-500' : ''}`}
                  disabled={scanMutation.isPending}
                />
                {!isValid && targetIP && (
                  <p className="mt-2 text-sm text-red-600 dark:text-red-400">
                    Please enter a valid IP address
                  </p>
                )}
                <p className="mt-2 text-sm text-gray-500 dark:text-gray-400">
                  Enter a single IP address for scanning
                </p>
              </div>

              {/* Scan Name */}
              <div className="mb-6">
                <div className="flex items-center justify-between mb-2">
                  <label className="block text-sm font-medium text-gray-700 dark:text-gray-300">
                    Scan Name
                  </label>
                  <div className="flex items-center">
                    <input
                      type="checkbox"
                      id="auto-generate"
                      checked={autoGenerateName}
                      onChange={(e) => setAutoGenerateName(e.target.checked)}
                      className="mr-2 rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                    />
                    <label htmlFor="auto-generate" className="text-sm text-gray-600 dark:text-gray-400">
                      Auto-generate name
                    </label>
                  </div>
                </div>
                <input
                  type="text"
                  value={scanName}
                  onChange={(e) => setScanName(e.target.value)}
                  placeholder={autoGenerateName ? "Auto-generated based on IP" : "Enter a custom scan name"}
                  className="input-field"
                  disabled={autoGenerateName || scanMutation.isPending}
                />
                <p className="mt-2 text-sm text-gray-500 dark:text-gray-400">
                  Give your scan a meaningful name for easy reference
                </p>
              </div>

              {/* Scan Details */}
              <div className="mb-8 p-4 bg-gray-50 dark:bg-gray-800/50 rounded-lg">
                <h3 className="font-medium text-gray-700 dark:text-gray-300 mb-3">Scan Details</h3>
                <div className="grid grid-cols-2 gap-4 text-sm">
                  <div>
                    <span className="text-gray-500 dark:text-gray-400">Ports to scan:</span>
                    <span className="ml-2 font-medium text-gray-900 dark:text-white">Top 50</span>
                  </div>
                  <div>
                    <span className="text-gray-500 dark:text-gray-400">Scan type:</span>
                    <span className="ml-2 font-medium text-gray-900 dark:text-white">Fast & Accurate</span>
                  </div>
                  <div>
                    <span className="text-gray-500 dark:text-gray-400">Estimated time:</span>
                    <span className="ml-2 font-medium text-gray-900 dark:text-white">20-30 seconds</span>
                  </div>
                  <div>
                    <span className="text-gray-500 dark:text-gray-400">Vulnerability check:</span>
                    <span className="ml-2 font-medium text-gray-900 dark:text-white">Enabled</span>
                  </div>
                </div>
              </div>

              {/* Submit Button */}
              <div className="flex justify-end">
                <button
                  type="submit"
                  disabled={!targetIP.trim() || !isValid || scanMutation.isPending}
                  className="btn-primary flex items-center px-6 py-3 text-lg"
                >
                  {scanMutation.isPending ? (
                    <>
                      <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white mr-3"></div>
                      Starting Scan...
                    </>
                  ) : (
                    <>
                      <FiZap className="mr-2" />
                      Start Vulnerability Scan
                    </>
                  )}
                </button>
              </div>
            </form>
          </div>
        </div>

        {/* Right Column - Features & Info */}
        <div className="space-y-6">
          {/* Features */}
          <div className="card">
            <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4">
              Why Use Our Scanner?
            </h3>
            <div className="space-y-4">
              {scanFeatures.map((feature, index) => (
                <div key={index} className="flex items-start space-x-3">
                  <div className="p-2 bg-gray-100 dark:bg-gray-800 rounded-lg mt-1">
                    {feature.icon}
                  </div>
                  <div>
                    <h4 className="font-medium text-gray-900 dark:text-white">
                      {feature.title}
                    </h4>
                    <p className="text-sm text-gray-600 dark:text-gray-400">
                      {feature.description}
                    </p>
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Tips */}
          <div className="card">
            <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4">
              Best Practices
            </h3>
            <ul className="space-y-3">
              <li className="flex items-start">
                <div className="flex-shrink-0 h-5 w-5 rounded-full bg-green-100 dark:bg-green-900 flex items-center justify-center mt-0.5 mr-3">
                  <div className="h-2 w-2 rounded-full bg-green-600"></div>
                </div>
                <span className="text-sm text-gray-600 dark:text-gray-400">
                  Only scan systems you own or have permission to test
                </span>
              </li>
              <li className="flex items-start">
                <div className="flex-shrink-0 h-5 w-5 rounded-full bg-green-100 dark:bg-green-900 flex items-center justify-center mt-0.5 mr-3">
                  <div className="h-2 w-2 rounded-full bg-green-600"></div>
                </div>
                <span className="text-sm text-gray-600 dark:text-gray-400">
                  Schedule scans during off-peak hours
                </span>
              </li>
              <li className="flex items-start">
                <div className="flex-shrink-0 h-5 w-5 rounded-full bg-green-100 dark:bg-green-900 flex items-center justify-center mt-0.5 mr-3">
                  <div className="h-2 w-2 rounded-full bg-green-600"></div>
                </div>
                <span className="text-sm text-gray-600 dark:text-gray-400">
                  Review and prioritize critical findings first
                </span>
              </li>
              <li className="flex items-start">
                <div className="flex-shrink-0 h-5 w-5 rounded-full bg-green-100 dark:bg-green-900 flex items-center justify-center mt-0.5 mr-3">
                  <div className="h-2 w-2 rounded-full bg-green-600"></div>
                </div>
                <span className="text-sm text-gray-600 dark:text-gray-400">
                  Export reports for compliance documentation
                </span>
              </li>
            </ul>
          </div>

          {/* Quick Stats */}
          <div className="card">
            <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4">
              Recent Activity
            </h3>
            <div className="space-y-3">
              <div className="flex justify-between items-center">
                <span className="text-sm text-gray-600 dark:text-gray-400">Scans this week:</span>
                <span className="font-semibold text-gray-900 dark:text-white">0</span>
              </div>
              <div className="flex justify-between items-center">
                <span className="text-sm text-gray-600 dark:text-gray-400">Average scan time:</span>
                <span className="font-semibold text-gray-900 dark:text-white">25s</span>
              </div>
              <div className="flex justify-between items-center">
                <span className="text-sm text-gray-600 dark:text-gray-400">Success rate:</span>
                <span className="font-semibold text-green-600">100%</span>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default NewScan;
