import React from 'react';
import { PieChart, Pie, Cell, ResponsiveContainer, Tooltip, Legend } from 'recharts';
import { severityColors } from '../../utils/api';

interface RiskDistributionData {
  critical: number;
  high: number;
  medium: number;
  low: number;
}

interface RiskDistributionChartProps {
  data?: RiskDistributionData;
}

const RiskDistributionChart: React.FC<RiskDistributionChartProps> = ({ data }) => {
  const chartData = [
    { name: 'Critical', value: data?.critical || 0, color: severityColors.critical.bg },
    { name: 'High', value: data?.high || 0, color: severityColors.high.bg },
    { name: 'Medium', value: data?.medium || 0, color: severityColors.medium.bg },
    { name: 'Low', value: data?.low || 0, color: severityColors.low.bg },
  ].filter(item => item.value > 0);

  const total = chartData.reduce((sum, item) => sum + item.value, 0);

  const CustomTooltip = ({ active, payload }: any) => {
    if (active && payload && payload.length) {
      const data = payload[0].payload;
      const percentage = total > 0 ? ((data.value / total) * 100).toFixed(1) : '0';
      
      return (
        <div className="bg-white dark:bg-gray-800 p-3 rounded-lg shadow-lg border border-gray-200 dark:border-gray-700">
          <p className="font-semibold" style={{ color: data.color }}>
            {data.name}
          </p>
          <p className="text-gray-700 dark:text-gray-300">
            {data.value} vulnerabilities
          </p>
          <p className="text-gray-500 dark:text-gray-400 text-sm">
            {percentage}% of total
          </p>
        </div>
      );
    }
    return null;
  };

  const CustomLegend = ({ payload }: any) => (
    <div className="flex flex-wrap justify-center gap-4 mt-4">
      {payload.map((entry: any, index: number) => (
        <div key={`legend-${index}`} className="flex items-center">
          <div 
            className="w-3 h-3 rounded-full mr-2" 
            style={{ backgroundColor: entry.color }}
          />
          <span className="text-sm text-gray-700 dark:text-gray-300">
            {entry.value} {entry.value === 1 ? 'vulnerability' : 'vulnerabilities'}
          </span>
        </div>
      ))}
    </div>
  );

  if (total === 0) {
    return (
      <div className="flex flex-col items-center justify-center h-80">
        <div className="text-center">
          <div className="text-4xl mb-4">ðŸ“Š</div>
          <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-2">
            No vulnerabilities detected
          </h3>
          <p className="text-gray-600 dark:text-gray-400">
            Run a scan to see vulnerability distribution
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="h-80">
      <ResponsiveContainer width="100%" height="100%">
        <PieChart>
          <Pie
            data={chartData}
            cx="50%"
            cy="50%"
            innerRadius={60}
            outerRadius={100}
            paddingAngle={2}
            dataKey="value"
            label={({ name, percent }) => 
              `${name}: ${(percent * 100).toFixed(0)}%`
            }
            labelLine={false}
          >
            {chartData.map((entry, index) => (
              <Cell key={`cell-${index}`} fill={entry.color} />
            ))}
          </Pie>
          <Tooltip content={<CustomTooltip />} />
          <Legend content={<CustomLegend />} />
        </PieChart>
      </ResponsiveContainer>
      
      {/* Summary Stats */}
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mt-6">
        {chartData.map((item, index) => (
          <div key={index} className="text-center">
            <div 
              className="text-2xl font-bold mb-1"
              style={{ color: item.color }}
            >
              {item.value}
            </div>
            <div className="text-sm text-gray-600 dark:text-gray-400">
              {item.name}
            </div>
            <div className="text-xs text-gray-500 dark:text-gray-500">
              {total > 0 ? `${((item.value / total) * 100).toFixed(1)}%` : '0%'}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default RiskDistributionChart;
