import React, { useEffect } from 'react';
import { useQuery } from '@tanstack/react-query';
import { 
  FiActivity, 
  FiCheckCircle, 
  FiAlertTriangle, 
  FiClock, 
  FiTrendingUp,
  FiRefreshCw 
} from 'react-icons/fi';
import { toast } from 'react-hot-toast';
import RiskDistributionChart from './RiskDistributionChart';
import RecentScansTable from './RecentScansTable';
import QuickStats from './QuickStats';
import { apiService } from '../../utils/api';
import { DashboardMetrics } from '../../types';

const Dashboard: React.FC = () => {
  const { data: metrics, isLoading, error, refetch: refetchMetrics } = useQuery<DashboardMetrics>({
    queryKey: ['dashboardMetrics'],
    queryFn: apiService.getDashboardMetrics,
    refetchInterval: 5000, // Refresh every 5 seconds
    refetchOnWindowFocus: true,
    refetchOnMount: true,
    staleTime: 3000,
  });

  const { data: stats, refetch: refetchStats } = useQuery({
    queryKey: ['quickStats'],
    queryFn: apiService.getQuickStats,
    refetchInterval: 5000, // Refresh every 5 seconds
    refetchOnWindowFocus: true,
    refetchOnMount: true,
    staleTime: 3000,
  });

  // Manual refresh function
  const handleManualRefresh = () => {
    refetchMetrics();
    refetchStats();
    toast.success('Dashboard refreshed');
  };

  // Auto-refresh when window regains focus
  useEffect(() => {
    const handleVisibilityChange = () => {
      if (!document.hidden) {
        refetchMetrics();
        refetchStats();
      }
    };

    document.addEventListener('visibilitychange', handleVisibilityChange);
    
    return () => {
      document.removeEventListener('visibilitychange', handleVisibilityChange);
    };
  }, [refetchMetrics, refetchStats]);

  if (isLoading) {
    return (
      <div className="space-y-6">
        <div className="flex items-center justify-between">
          <div>
            <div className="h-8 w-48 bg-gray-200 dark:bg-gray-700 rounded animate-pulse"></div>
            <div className="h-4 w-64 bg-gray-200 dark:bg-gray-700 rounded animate-pulse mt-2"></div>
          </div>
          <div className="h-10 w-40 bg-gray-200 dark:bg-gray-700 rounded animate-pulse"></div>
        </div>
        
        {/* Quick Stats Skeleton */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
          {[1, 2, 3, 4].map((i) => (
            <div key={i} className="card h-32 animate-pulse">
              <div className="h-4 w-24 bg-gray-200 dark:bg-gray-700 rounded mb-4"></div>
              <div className="h-10 w-16 bg-gray-200 dark:bg-gray-700 rounded"></div>
            </div>
          ))}
        </div>
        
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          <div className="lg:col-span-2 card h-96 animate-pulse"></div>
          <div className="card h-96 animate-pulse"></div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="card text-center p-12">
        <FiAlertTriangle className="mx-auto text-red-500" size={48} />
        <h3 className="text-xl font-semibold text-gray-900 dark:text-white mt-4">
          Failed to load dashboard
        </h3>
        <p className="text-gray-600 dark:text-gray-400 mt-2">
          Please check your connection and try again.
        </p>
        <button
          onClick={handleManualRefresh}
          className="mt-4 inline-flex items-center px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
        >
          <FiRefreshCw className="mr-2" />
          Refresh Dashboard
        </button>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header with refresh button */}
      <div className="flex flex-col md:flex-row md:items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-gray-900 dark:text-white">Dashboard</h1>
          <p className="text-gray-600 dark:text-gray-400 mt-2">
            Real-time overview of your security scans and vulnerabilities
            <span className="ml-2 text-xs text-green-600 dark:text-green-400 animate-pulse">
              â€¢ Live
            </span>
          </p>
        </div>
        <div className="flex space-x-3 mt-4 md:mt-0">
          <div className="text-sm text-gray-500 dark:text-gray-400 flex items-center">
            <FiClock className="mr-1" size={14} />
            Auto-refresh: 5s
          </div>
          <button
            onClick={handleManualRefresh}
            className="flex items-center px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
          >
            <FiRefreshCw className="mr-2" />
            Refresh Now
          </button>
        </div>
      </div>

      {/* Quick Stats */}
      <QuickStats stats={stats} />

      {/* Main Content */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Risk Distribution - Takes 2/3 on large screens */}
        <div className="lg:col-span-2">
          <div className="card">
            <div className="flex items-center justify-between mb-6">
              <div>
                <h2 className="text-xl font-semibold text-gray-900 dark:text-white">
                  Risk Distribution
                </h2>
                <p className="text-gray-600 dark:text-gray-400 text-sm">
                  Severity breakdown of detected vulnerabilities
                </p>
              </div>
              <div className="flex items-center space-x-2 text-gray-600 dark:text-gray-400">
                <FiTrendingUp />
                <span className="text-sm">Updated just now</span>
              </div>
            </div>
            <RiskDistributionChart data={metrics?.riskDistribution} />
          </div>
        </div>

        {/* Recent Activity Sidebar */}
        <div className="space-y-6">
          {/* Total Scans Card */}
          <div className="card">
            <div className="flex items-center justify-between mb-4">
              <h3 className="font-semibold text-gray-900 dark:text-white">Total Scans</h3>
              <FiActivity className="text-blue-600" size={20} />
            </div>
            <div className="text-4xl font-bold text-gray-900 dark:text-white">
              {metrics?.totalScans || 0}
            </div>
            <div className="mt-2 text-sm text-gray-600 dark:text-gray-400">
              {metrics?.completedScans || 0} completed
            </div>
            <div className="mt-2 text-xs text-gray-500 dark:text-gray-500">
              Last updated: Just now
            </div>
          </div>

          {/* Last Scan Card */}
          <div className="card">
            <div className="flex items-center justify-between mb-4">
              <h3 className="font-semibold text-gray-900 dark:text-white">Last Scan</h3>
              <FiClock className="text-green-600" size={20} />
            </div>
            {metrics?.lastScan?.timestamp ? (
              <>
                <div className="text-lg font-semibold text-gray-900 dark:text-white">
                  {metrics.lastScan.target || 'Unknown'}
                </div>
                <div className="mt-1 text-sm text-gray-600 dark:text-gray-400">
                  {new Date(metrics.lastScan.timestamp).toLocaleString()}
                </div>
                <div className="mt-2">
                  <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
                    metrics.lastScan.status === 'completed' 
                      ? 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200'
                      : metrics.lastScan.status === 'running'
                      ? 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200'
                      : 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200'
                  }`}>
                    {metrics.lastScan.status || 'Unknown'}
                  </span>
                </div>
              </>
            ) : (
              <div className="text-gray-500 dark:text-gray-400 italic">No scans yet</div>
            )}
          </div>

          {/* Avg Vulnerabilities Card */}
          <div className="card">
            <div className="flex items-center justify-between mb-4">
              <h3 className="font-semibold text-gray-900 dark:text-white">Avg per Scan</h3>
              <FiCheckCircle className="text-purple-600" size={20} />
            </div>
            <div className="text-4xl font-bold text-gray-900 dark:text-white">
              {metrics?.avgVulnerabilities || 0}
            </div>
            <div className="mt-2 text-sm text-gray-600 dark:text-gray-400">
              vulnerabilities per scan
            </div>
            <div className="mt-2 text-xs text-gray-500 dark:text-gray-500">
              Based on {metrics?.completedScans || 0} scans
            </div>
          </div>
        </div>
      </div>

      {/* Recent Scans Table */}
      <div className="card">
        <div className="flex items-center justify-between mb-6">
          <h2 className="text-xl font-semibold text-gray-900 dark:text-white">
            Recent Scans
          </h2>
          <button
            onClick={handleManualRefresh}
            className="text-sm text-blue-600 hover:text-blue-800 dark:text-blue-400 dark:hover:text-blue-300"
          >
            Refresh table
          </button>
        </div>
        <RecentScansTable scans={metrics?.recentScans} />
      </div>

      {/* Debug info - only in development */}
      {process.env.NODE_ENV === 'development' && (
        <div className="card bg-gray-50 dark:bg-gray-800/30 text-xs">
          <div className="text-gray-500 dark:text-gray-400">Dashboard Debug Info:</div>
          <div className="mt-1">
            <span className="text-gray-600 dark:text-gray-300">Last fetch: </span>
            <span className="text-green-600 dark:text-green-400">Just now</span>
          </div>
          <div>
            <span className="text-gray-600 dark:text-gray-300">Total scans in data: </span>
            <span className="text-blue-600 dark:text-blue-400">{metrics?.recentScans?.length || 0}</span>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
