import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useMutation, useQueryClient } from '@tanstack/react-query';
import { FiTarget, FiPlay, FiAlertCircle, FiCheckCircle, FiX, FiActivity, FiBarChart2 } from 'react-icons/fi';
import { toast } from 'react-hot-toast';
import { apiService, isValidIP } from '../../utils/api';
import { useWebSocket } from '../../hooks/useWebSocket';

interface ScanProgress {
  progress: number;
  phase: string;
  message: string;
  timestamp: string;
}

const NewScan: React.FC = () => {
  const [targetIp, setTargetIp] = useState('');
  const [scanName, setScanName] = useState('');
  const [isScanning, setIsScanning] = useState(false);
  const [scanProgress, setScanProgress] = useState<ScanProgress>({
    progress: 0,
    phase: 'idle',
    message: 'Ready to scan',
    timestamp: new Date().toISOString()
  });
  const [scanId, setScanId] = useState<string | null>(null);
  
  const navigate = useNavigate();
  const queryClient = useQueryClient();

  // Start scan mutation
  const startScanMutation = useMutation({
    mutationFn: () => apiService.startScan(targetIp, scanName || undefined),
    onSuccess: (data) => {
      setScanId(data.scan_id);
      setIsScanning(true);
      setScanProgress({
        progress: 0,
        phase: 'initializing',
        message: 'Starting scan...',
        timestamp: new Date().toISOString()
      });
      
      toast.success('Scan started successfully! You can navigate away - updates will continue.');
      
      // Pre-invalidate queries so they refetch when scan completes
      queryClient.invalidateQueries({ queryKey: ['dashboardMetrics'] });
      queryClient.invalidateQueries({ queryKey: ['quickStats'] });
    },
    onError: (error: any) => {
      toast.error(`Failed to start scan: ${error.message}`);
      setIsScanning(false);
    },
  });

  // WebSocket connection for this scan
  const { 
    isConnected: wsConnected, 
    lastMessage: wsMessage,
    sendMessage 
  } = useWebSocket(scanId || '');

  // Handle WebSocket messages
  useEffect(() => {
    if (wsMessage) {
      console.log('Scan WebSocket message:', wsMessage);
      
      switch (wsMessage.type) {
        case 'scan_update':
          setScanProgress({
            progress: wsMessage.data.progress || 0,
            phase: wsMessage.data.phase || '',
            message: wsMessage.data.message || '',
            timestamp: wsMessage.timestamp
          });
          break;
        
        case 'scan_completed':
          setScanProgress({
            progress: 100,
            phase: 'completed',
            message: 'Scan completed successfully!',
            timestamp: wsMessage.timestamp
          });
          
          // Show success message with navigation options
          toast.success(
            <div>
              <div className="font-semibold">Scan Completed!</div>
              <div className="text-sm mt-1">
                <button 
                  onClick={() => navigate(`/reports/${scanId}`)}
                  className="text-blue-600 hover:text-blue-800 mr-3"
                >
                  View Report
                </button>
                <button 
                  onClick={() => navigate('/dashboard')}
                  className="text-green-600 hover:text-green-800 mr-3"
                >
                  Go to Dashboard
                </button>
              </div>
            </div>,
            { duration: 5000 }
          );
          
          // Force refresh all related data
          setTimeout(() => {
            queryClient.invalidateQueries({ queryKey: ['dashboardMetrics'], refetchType: 'all' });
            queryClient.invalidateQueries({ queryKey: ['quickStats'], refetchType: 'all' });
            queryClient.invalidateQueries({ queryKey: ['reports'], refetchType: 'all' });
            queryClient.invalidateQueries({ queryKey: ['scans'], refetchType: 'all' });
          }, 1000);
          
          // Auto-navigate to reports after 3 seconds
          setTimeout(() => {
            navigate(`/reports/${scanId}`);
          }, 3000);
          break;
        
        case 'scan_failed':
          toast.error(`Scan failed: ${wsMessage.error}`);
          setIsScanning(false);
          break;
        
        case 'scan_aborted':
          toast.info('Scan was aborted');
          setIsScanning(false);
          setScanId(null);
          break;
      }
    }
  }, [wsMessage, scanId, navigate, queryClient]);

  // Auto-connect WebSocket when scanId is set
  useEffect(() => {
    if (scanId && isScanning) {
      // Send a ping to verify connection
      setTimeout(() => {
        if (sendMessage) {
          sendMessage({ type: 'ping' });
        }
      }, 1000);
    }
  }, [scanId, isScanning, sendMessage]);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    
    if (!targetIp.trim()) {
      toast.error('Please enter a target IP address');
      return;
    }

    if (!isValidIP(targetIp)) {
      toast.error('Please enter a valid IP address');
      return;
    }

    startScanMutation.mutate();
  };

  const handleCancel = () => {
    if (scanId && isScanning) {
      // You can implement abort functionality here
      toast.info('Scan cancellation requested');
      setIsScanning(false);
    }
  };

  // Auto-generate scan name
  useEffect(() => {
    if (targetIp && !scanName) {
      const timestamp = new Date().toLocaleString('en-US', {
        month: 'short',
        day: 'numeric',
        hour: '2-digit',
        minute: '2-digit',
      });
      setScanName(`Scan ${targetIp} - ${timestamp}`);
    }
  }, [targetIp, scanName]);

  return (
    <div className="max-w-4xl mx-auto">
      <div className="flex items-center justify-between mb-8">
        <div>
          <h1 className="text-3xl font-bold text-gray-900 dark:text-white">New Vulnerability Scan</h1>
          <p className="text-gray-600 dark:text-gray-400 mt-2">
            {isScanning ? 'Real-time scanning in progress...' : 'Quick and focused security assessment'}
          </p>
        </div>
        
        {/* Connection Status */}
        <div className={`flex items-center px-3 py-1 rounded-full text-sm ${
          wsConnected 
            ? 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200'
            : isScanning
            ? 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200'
            : 'bg-gray-100 text-gray-800 dark:bg-gray-900 dark:text-gray-200'
        }`}>
          <div className={`w-2 h-2 rounded-full mr-2 ${
            wsConnected ? 'bg-green-500 animate-pulse' : 
            isScanning ? 'bg-yellow-500' : 'bg-gray-500'
          }`}></div>
          {wsConnected ? 'Live Connected' : isScanning ? 'Connecting...' : 'Ready'}
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Left Column - Scan Configuration */}
        <div className="lg:col-span-2">
          <div className="card">
            <h2 className="text-xl font-semibold text-gray-900 dark:text-white mb-6">
              {isScanning ? 'Scan in Progress' : 'Scan Configuration'}
            </h2>
            
            <form onSubmit={handleSubmit}>
              {/* Target IP */}
              <div className="mb-6">
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                  Target IP Address *
                </label>
                <div className="relative">
                  <FiTarget className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
                  <input
                    type="text"
                    value={targetIp}
                    onChange={(e) => setTargetIp(e.target.value)}
                    placeholder="e.g., 192.168.1.1"
                    className="pl-10 pr-4 py-3 w-full border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-800 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    disabled={isScanning}
                  />
                </div>
                <p className="mt-2 text-sm text-gray-500 dark:text-gray-400">
                  Enter a single IP address for scanning
                </p>
              </div>

              {/* Scan Name */}
              <div className="mb-8">
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                  Scan Name
                </label>
                <input
                  type="text"
                  value={scanName}
                  onChange={(e) => setScanName(e.target.value)}
                  placeholder="Auto-generated based on IP"
                  className="px-4 py-3 w-full border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-800 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  disabled={isScanning}
                />
                <p className="mt-2 text-sm text-gray-500 dark:text-gray-400">
                  Give your scan a meaningful name for easy reference
                </p>
              </div>

              {/* Scan Progress (if scanning) */}
              {isScanning && (
                <div className="mb-8 p-6 bg-gradient-to-r from-blue-50 to-indigo-50 dark:from-blue-900/20 dark:to-indigo-900/20 rounded-xl border border-blue-200 dark:border-blue-800">
                  <div className="flex items-center justify-between mb-4">
                    <div className="flex items-center">
                      <div className="w-3 h-3 rounded-full bg-blue-500 animate-pulse mr-3"></div>
                      <div>
                        <h3 className="font-bold text-blue-800 dark:text-blue-300">
                          {scanProgress.phase === 'completed' ? 'âœ“ Scan Complete' : 'Scan in Progress'}
                        </h3>
                        <p className="text-sm text-blue-600 dark:text-blue-400 mt-1">
                          ID: {scanId?.substring(0, 8)}...
                        </p>
                      </div>
                    </div>
                    <div className="text-right">
                      <div className="text-2xl font-bold text-blue-700 dark:text-blue-300">
                        {scanProgress.progress}%
                      </div>
                      <div className="text-xs text-blue-600 dark:text-blue-400">
                        {wsConnected ? 'Live' : 'Connecting...'}
                      </div>
                    </div>
                  </div>
                  
                  {/* Progress Bar */}
                  <div className="w-full bg-blue-100 dark:bg-blue-900/30 rounded-full h-3 mb-4">
                    <div
                      className="bg-gradient-to-r from-blue-500 to-indigo-600 h-3 rounded-full transition-all duration-500 ease-out"
                      style={{ width: `${scanProgress.progress}%` }}
                    ></div>
                  </div>
                  
                  <div className="flex justify-between text-sm">
                    <div className="text-blue-700 dark:text-blue-300 font-medium">
                      {scanProgress.message}
                    </div>
                    <div className="text-blue-600 dark:text-blue-400">
                      Phase: {scanProgress.phase}
                    </div>
                  </div>
                  
                  {/* Navigation Hint */}
                  <div className="mt-6 p-3 bg-white/50 dark:bg-gray-800/50 rounded-lg">
                    <div className="text-sm text-gray-700 dark:text-gray-300">
                      <span className="font-semibold">ðŸ’¡ Tip:</span> You can navigate to Dashboard or Reports while this scan runs.
                      Updates will appear automatically.
                    </div>
                    <div className="flex space-x-3 mt-3">
                      <button
                        type="button"
                        onClick={() => navigate('/dashboard')}
                        className="flex-1 flex items-center justify-center px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors text-sm"
                      >
                        <FiActivity className="mr-2" />
                        View Dashboard
                      </button>
                      <button
                        type="button"
                        onClick={() => navigate('/reports')}
                        className="flex-1 flex items-center justify-center px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 transition-colors text-sm"
                      >
                        <FiBarChart2 className="mr-2" />
                        View Reports
                      </button>
                    </div>
                  </div>
                </div>
              )}

              {/* Action Buttons */}
              <div className="flex space-x-3">
                <button
                  type="submit"
                  disabled={isScanning || !targetIp.trim() || startScanMutation.isPending}
                  className="btn-primary flex-1 flex items-center justify-center py-3"
                >
                  {isScanning ? (
                    <>
                      <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white mr-2"></div>
                      Scanning...
                    </>
                  ) : (
                    <>
                      <FiPlay className="mr-2" />
                      Start Vulnerability Scan
                    </>
                  )}
                </button>
                
                <button
                  type="button"
                  onClick={() => navigate('/dashboard')}
                  className="btn-secondary flex items-center px-6"
                >
                  <FiAlertCircle className="mr-2" />
                  View Dashboard
                </button>
              </div>
            </form>
          </div>
        </div>

        {/* Right Column - Scan Details */}
        <div className="space-y-6">
          {/* Scan Details Card */}
          <div className="card">
            <h3 className="font-semibold text-gray-900 dark:text-white mb-4">Scan Details</h3>
            <ul className="space-y-3 text-sm text-gray-600 dark:text-gray-400">
              <li className="flex justify-between">
                <span>Ports to scan:</span>
                <span className="font-medium">Top 50</span>
              </li>
              <li className="flex justify-between">
                <span>Estimated time:</span>
                <span className="font-medium">20-30 seconds</span>
              </li>
              <li className="flex justify-between">
                <span>Scan type:</span>
                <span className="font-medium text-green-600">Fast & Accurate</span>
              </li>
              <li className="flex justify-between">
                <span>Vulnerability check:</span>
                <span className="font-medium text-green-600">Enabled</span>
              </li>
              {scanId && (
                <li className="flex justify-between pt-3 border-t border-gray-200 dark:border-gray-700">
                  <span>Scan ID:</span>
                  <span className="font-mono text-xs">{scanId.substring(0, 8)}...</span>
                </li>
              )}
            </ul>
          </div>

          {/* Live Connection Panel */}
          {isScanning && (
            <div className="card">
              <h3 className="font-semibold text-gray-900 dark:text-white mb-4">
                <div className="flex items-center">
                  <div className={`w-2 h-2 rounded-full mr-2 ${
                    wsConnected ? 'bg-green-500 animate-pulse' : 'bg-yellow-500'
                  }`}></div>
                  Live Connection
                </div>
              </h3>
              
              <div className="space-y-4">
                <div className="flex justify-between text-sm">
                  <span className="text-gray-600 dark:text-gray-400">Status:</span>
                  <span className={`font-medium ${
                    wsConnected ? 'text-green-600' : 'text-yellow-600'
                  }`}>
                    {wsConnected ? 'Connected' : 'Connecting...'}
                  </span>
                </div>
                
                <div className="flex justify-between text-sm">
                  <span className="text-gray-600 dark:text-gray-400">Progress:</span>
                  <span className="font-medium">{scanProgress.progress}%</span>
                </div>
                
                <div className="flex justify-between text-sm">
                  <span className="text-gray-600 dark:text-gray-400">Phase:</span>
                  <span className="font-medium capitalize">{scanProgress.phase}</span>
                </div>
                
                {/* Real-time Updates Info */}
                <div className="mt-4 p-3 bg-gray-50 dark:bg-gray-800/50 rounded-lg">
                  <div className="text-xs text-gray-600 dark:text-gray-400">
                    <div className="font-medium mb-1">Real-time Features:</div>
                    <div className="space-y-1">
                      <div className="flex items-center">
                        <FiCheckCircle className="w-3 h-3 mr-2 text-green-500" />
                        <span>Dashboard auto-updates</span>
                      </div>
                      <div className="flex items-center">
                        <FiCheckCircle className="w-3 h-3 mr-2 text-green-500" />
                        <span>Reports update instantly</span>
                      </div>
                      <div className="flex items-center">
                        <FiCheckCircle className="w-3 h-3 mr-2 text-green-500" />
                        <span>Switch tabs freely</span>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          )}

          {/* Quick Actions */}
          <div className="card">
            <h3 className="font-semibold text-gray-900 dark:text-white mb-4">Quick Actions</h3>
            <div className="space-y-3">
              <button
                onClick={() => navigate('/dashboard')}
                className="w-full flex items-center justify-between p-3 rounded-lg bg-gray-50 dark:bg-gray-800 hover:bg-gray-100 dark:hover:bg-gray-700 transition-colors"
              >
                <div className="flex items-center">
                  <FiActivity className="text-blue-600 mr-3" />
                  <span>View Dashboard</span>
                </div>
                <span className="text-sm text-gray-500">Live updates</span>
              </button>
              
              <button
                onClick={() => navigate('/reports')}
                className="w-full flex items-center justify-between p-3 rounded-lg bg-gray-50 dark:bg-gray-800 hover:bg-gray-100 dark:hover:bg-gray-700 transition-colors"
              >
                <div className="flex items-center">
                  <FiBarChart2 className="text-green-600 mr-3" />
                  <span>View Reports</span>
                </div>
                <span className="text-sm text-gray-500">All scans</span>
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default NewScan;
