import React from 'react';
import { PieChart, Pie, Cell, ResponsiveContainer, Tooltip } from 'recharts';
import { severityColors } from '../../utils/api';

interface RiskDistributionData {
  critical: number;
  high: number;
  medium: number;
  low: number;
}

interface RiskDistributionChartProps {
  data?: RiskDistributionData;
}

const RiskDistributionChart: React.FC<RiskDistributionChartProps> = ({ data }) => {
  const chartData = [
    { name: 'Critical', value: data?.critical || 0, color: severityColors.critical.bg },
    { name: 'High', value: data?.high || 0, color: severityColors.high.bg },
    { name: 'Medium', value: data?.medium || 0, color: severityColors.medium.bg },
    { name: 'Low', value: data?.low || 0, color: severityColors.low.bg },
  ].filter(item => item.value > 0);

  const total = chartData.reduce((sum, item) => sum + item.value, 0);

  const CustomTooltip = ({ active, payload }: any) => {
    if (active && payload && payload.length) {
      const data = payload[0].payload;
      const percentage = total > 0 ? ((data.value / total) * 100).toFixed(1) : '0';
      
      return (
        <div className="bg-white dark:bg-gray-800 p-3 rounded-lg shadow-lg border border-gray-200 dark:border-gray-700">
          <p className="font-semibold" style={{ color: data.color }}>
            {data.name}
          </p>
          <p className="text-gray-700 dark:text-gray-300">
            {data.value} vulnerabilities
          </p>
          <p className="text-gray-500 dark:text-gray-400 text-sm">
            {percentage}% of total
          </p>
        </div>
      );
    }
    return null;
  };

  if (total === 0) {
    return (
      <div className="flex flex-col items-center justify-center h-80">
        <div className="text-center">
          <div className="text-4xl mb-4">ðŸ“Š</div>
          <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-2">
            No vulnerabilities detected
          </h3>
          <p className="text-gray-600 dark:text-gray-400">
            Run a scan to see vulnerability distribution
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="h-80">
      <ResponsiveContainer width="100%" height="100%">
        <PieChart margin={{ top: 20, right: 20, bottom: 20, left: 20 }}>
          <Pie
            data={chartData}
            cx="50%"
            cy="50%"
            innerRadius={40}  // Smaller inner radius
            outerRadius={70}   // Smaller outer radius
            paddingAngle={1}
            dataKey="value"
            label={({ name, percent, x, y, cx, cy }) => {
              // Calculate position to keep labels inside
              const radius = 70;
              const angle = Math.atan2(y - cy, x - cx);
              const labelRadius = radius * 0.85; // Position labels slightly inside
              
              const labelX = cx + labelRadius * Math.cos(angle);
              const labelY = cy + labelRadius * Math.sin(angle);
              
              return (
                <text
                  x={labelX}
                  y={labelY}
                  fill="currentColor"
                  textAnchor="middle"
                  dominantBaseline="central"
                  className="text-xs font-medium fill-gray-700 dark:fill-gray-300"
                >
                  {`${(percent * 100).toFixed(0)}%`}
                </text>
              );
            }}
            labelLine={false} // Remove label lines entirely
          >
            {chartData.map((entry, index) => (
              <Cell key={`cell-${index}`} fill={entry.color} />
            ))}
          </Pie>
          <Tooltip content={<CustomTooltip />} />
        </PieChart>
      </ResponsiveContainer>
      
      {/* Summary Stats - Cleaner version */}
      <div className="mt-6 pt-4 border-t border-gray-200 dark:border-gray-700">
        <div className="grid grid-cols-2 md:grid-cols-4 gap-3">
          {chartData.map((item, index) => (
            <div key={index} className="text-center p-3 bg-gray-50 dark:bg-gray-800/50 rounded-lg">
              <div 
                className="text-xl font-bold mb-1"
                style={{ color: item.color }}
              >
                {item.value}
              </div>
              <div className="text-sm font-medium text-gray-700 dark:text-gray-300">
                {item.name}
              </div>
              <div className="text-xs text-gray-500 dark:text-gray-500 mt-1">
                {total > 0 ? `${((item.value / total) * 100).toFixed(1)}%` : '0%'}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default RiskDistributionChart;
